'use strict';

var _utils = require('./utils');

var data = [{
  id: 1
}, {
  id: 2
}, {
  id: 3
}];

describe('utils', function () {

  it('should return an item if it exists', function () {
    var item = (0, _utils.only)(data[0], data);
    expect(item).toEqual(data[0]);
  });

  it('should return an item if it exists', function () {
    var item = (0, _utils.only)({ id: 5 }, data);
    expect(item).toEqual(undefined);
  });
});